apidoc:example.pre:
xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-manage("/foo/bar/baz.xml", 
                    fn:false(), 
                    "Baz is now a managed document") 

(: Makes 'baz.xml' a managed document, but not its referenced documents. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-unmanage("/foo/bar/baz.xml", fn:false(), fn:true())
(: Unmanages 'baz.xml', but maintains management of its referenced documents. All versions but the latest version of this document are deleted. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-checkout("/foo/bar/baz.xml", fn:true(), "updating doc", 3600)

(: Checks out the 'baz'xml' document, and all of its included documents,
   for one hour. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-checkin("/foo/bar/baz.xml", fn:true())

(: Checks in 'baz.xml' and all of its included documents. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-version-as-of( "/foo/bar/baz.xml", xs:dateTime("2009-04-07T09:43:16.531-07:00"))
(: Returns the most recent version of the 'baz.xml' document since the specified date and time. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-update(
         "/foo/bar/baz.xml",
          dls:document-version( "/foo/bar/baz.xml", 1),
          "restoring from version 1", 
          fn:true() )

(: Restores version 1 of the 'baz.xml' document. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-version-uris("/foo/bar/baz.xml")

(: Returns the URIs of all versions of the '/foo/bar/baz.xml' document. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-version-delete("/foo/bar/baz.xml", 1, fn:true())

(: Deletes version 1 of the 'baz.xml' document and records it in the document
   history. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

cts:search(fn:doc(), dls:document-versions-query("/foo/bar/baz.xml"))

(: Returns the contents of all versions of the 'baz.xml' document. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-version-uri("/foo/bar/baz.xml", 2)

=>

/foo/bar/baz.xml_versions/2-baz.xml

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-delete("/foo/bar/baz.xml", fn:false(), fn:true())

(: Deletes all versions of the 'baz.xml' document and retains the
   property fragment for each version. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-is-managed("/foo/bar/baz.xml")

(: Returns true if document is managed.  Otherwise false. :)

xquery version "1.0-ml"; import module namespace dls="http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:retention-rule( "All Versions Retention Rule", "Retain all versions of all documents", (), (), "Locate all of the documents", cts:and-query(() )
(: Returns a retention rule in XML format. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:retention-rule( "Rule1", "Keep the five most recent versions of Jim's documents that are 15 days old or newer", 5, xs:duration("P15D"), "Locate documents authored by Jim", dls:author-query(xdmp:user("Jim"))))
(: Returns a retention rule in XML format. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:retention-rule( "Rule2", "Keep the five most recent versions of Jim's documents in the 'foo' collection that are 15 days old or newer", 5, xs:duration("P15D"), "Locate documents in the 'foo' collection authored by Jim", cts:and-query(( cts:collection-query("http://marklogic.com/documents/foo"), dls:author-query(xdmp:user("Jim")) )) )
(: Returns a retention rule in XML format. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:retention-rule-insert(( dls:retention-rule( "Rule1", "Keep the five most recent versions of Jim's documents that are 15 days old or newer", 5, xs:duration("P15D"), "Locate all documents authored by Jim", dls:author-query(xdmp:user("Jim"))),
dls:retention-rule( "Rule2", "Retain the five most recent versions of documents that match the query below", 5, (), "Locate only docs that include 'Baz' and 'Disco'" , cts:and-query(( cts:word-query("Baz"), cts:word-query("Disco"))) ) ))
(: Inserts the retention rules, 'Rule1' and 'Rule2', into the database. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:retention-rules("Rule1")

(: Returns the 'Rule1' retention rule in XML format. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:retention-rule-remove(("Rule1", "Rule2"))

(: Removes the retention rules, 'Rule1' and 'Rule2'. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:retention-rule-remove(fn:data(dls:retention-rules("*")//dls:name))

(: Removes all of the retention rules. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-retention-rules("/foo/bar/baz.xml")

(: Returns all of the retention rules that apply to the 'baz.xml' document. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-purge(fn:true(), fn:true())

(: Deletes all of the versions of the 'baz.xml' document and its 
   referenced documents. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-purge("/foo/bar/baz.xml", fn:false(), fn:true())

(: Returns the versions of the 'baz.xml' document and its referenced 
   documents, as specified by the rention policy. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

cts:search(fn:doc(), dls:as-of-query(xs:dateTime(fn:current-dateTime())))

(: Returns the versions of the managed documents that were created before the
   specified date and time. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

cts:search(fn:doc(), dls:author-query(xdmp:user("Jim")))

(: Returns all of the documents authored by 'Jim'. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

cts:search(fn:doc(), dls:document-version-query(3))

(: Returns Version 3 of all of the managed documents in the database. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

cts:search(fn:doc(), dls:documents-query()) 

(: Returns the latest versions of the managed documents in the database. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

declare namespace xi="http://www.w3.org/2001/XInclude";

let $node := fn:doc("/foo/bar/baz.xml")

return dls:link-expand($node, 
                       $node/BOOK/xi:include[1], 
                       dls:documents-query())

(: Returns the first referenced node in the 'BOOK' element in the 
   latest version of the 'baz.xml' document. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

let $node := fn:doc("/foo/bar/baz.xml")

return dls:link-references($node, dls:document-version-query(4))

(: Returns the URIs of the referenced nodes in Version 4 of the 'baz.xml' 
   document and all of the referenced nodes referenced from its referenced 
   nodes. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

let $node := fn:doc("/foo/bar/baz.xml")

return dls:node-expand(
             $node, 
             dls:as-of-query(xs:dateTime("2009-04-06T09:39:33.576-07:00")))

(: Returns the contents of the 'baz.xml' document version that was created 
   before the specified date, along with all of its referenced nodes. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-add-collections( "/foo/bar/baz.xml", "http://marklogic.com/documents/foo")
(: Adds 'bax.xml' to the 'foo' collection. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-add-permissions("/foo/bar/baz.xml", (xdmp:permission("dls-user", "read"), xdmp:permission("dls-user", "update")))
(: Adds the specified permissions to the list of permissions for the 'baz.xml' document. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-get-permissions("/foo/bar/baz.xml")

(: Returns the permissions for the 'baz.xml' document. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
xdmp:document-add-properties( "/foo/bar/baz.xml", (<priority>1</priority>, <status>unedited</status>))
(: The <priority> and <status> properties are added to the 'baz.xml' document. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-set-collections( "/foo/bar/baz.xml", "http://marklogic.com/documents/foo")
(: Sets 'bax.xml' to the 'foo' collection. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-set-permissions("/foo/bar/baz.xml", (xdmp:permission("dls-user", "read"), xdmp:permission("dls-user", "update"), xdmp:permission("Developer", "read"), xdmp:permission("Developer", "update")))
(: Sets the permissions on the 'baz.xml' document. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
xdmp:document-set-properties( "/foo/bar/baz.xml", (<priority>1</priority>, <status>unedited</status>))
(: The <priority> and <status> properties are set to the 'baz.xml' document. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-set-property( "/foo/bar/baz.xml", <priority>1</priority>)
(: The <priority> property is set to the 'baz.xml' document. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-set-quality("/foo/bar/baz.xml", 10)

(: Sets the quality of the 'baz.xml' document to 10. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-remove-collections( "/foo/bar/baz.xml", "http://marklogic.com/documents/foo")
(: Removes 'bax.xml' from the 'foo' collection. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
dls:document-remove-permissions( "/foo/bar/baz.xml", (xdmp:permission("Developer", "read"), xdmp:permission("Developer", "update")))
(: Removes the specified permissions from the list of permissions for the 'baz.xml' document. :)

xquery version "1.0-ml";

import module namespace dls = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/dls.xqy";

dls:document-remove-properties(
       "/foo/bar/baz.xml", 
       (fn:QName("http://marklogic.com/xdmp/property", "priority"),
        fn:QName("http://marklogic.com/xdmp/property", "status")))

(: The <priority> and <status> properties are removed from the 
   'baz.xml' document. :)

xquery version "1.0-ml";
import module namespace admin = "http://marklogic.com/xdmp/dls" at "/MarkLogic/admin.xqy";
let $bazbook := <BOOK> <TITLE>Baz Goes to the Disco</TITLE> <CHAPTER1> <TITLE>Baz Wakes Up</TITLE> <PARA> Baz woke up this afternoon to the sound of James Brown. Soon Baz was feeling a little funky, so he put on his cleanest propeller hat and headed out in search of a Disco. </PARA> </CHAPTER1> </BOOK>
return dls:document-update( "/foo/bar/baz.xml", $bazbook, "Changed the title from Baz Feelin' Funky", fn:true())
(: Updates the contents of 'baz.xml'. :)

xquery version "1.0-ml";

import module namespace admin = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/admin.xqy";

dls:break-checkout("/foo/bar/baz.xml", fn:true())

(: This breaks the checkout of the 'baz.xml' document. :)

xquery version "1.0-ml";
import module namespace admin = "http://marklogic.com/xdmp/dls" at "/MarkLogic/admin.xqy";
dls:document-extract-part("/foo/bar/baz-chapter1.xml", fn:doc("/foo/bar/baz.xml")//CHAPTER1, "part insert", fn:true(), (xdmp:permission("dls-user","insert"), xdmp:permission("dls-user","read"), xdmp:permission("dls-user","update"), xdmp:permission("admin","insert"), xdmp:permission("admin","read"), xdmp:permission("admin","update")))
(: Extracts the <CHAPTER1> element from the 'baz.xml' file and creates a new document, named 'baz-chapter1.xml', containing the extracted element. :)

xquery version "1.0-ml";
import module namespace admin = "http://marklogic.com/xdmp/dls" at "/MarkLogic/admin.xqy";
dls:document-checkout-status("/foo/bar/baz.xml")
=>
<dls:checkout xmlns:dls="http://marklogic.com/xdmp/dls"> <dls:document-uri>/foo/bar/baz.xml</dls:document-uri> <dls:annotation>updating doc</dls:annotation> <dls:timeout>3600</dls:timeout> <dls:timestamp>1239057381</dls:timestamp> <sec:user-id xmlns:sec="http://marklogic.com/xdmp/security">10677693687367813363</sec:user-id> </dls:checkout>

xquery version "1.0-ml";

import module namespace admin = "http://marklogic.com/xdmp/dls" 
    at "/MarkLogic/admin.xqy";

cts:search(fn:doc(), dls:document-include-query("/foo.xml"))

(: Returns the contents of the documents that contain an XInclude to the
   '/foo.xml' document. :)

xquery version "1.0-ml";
import module namespace dls = "http://marklogic.com/xdmp/dls" at "/MarkLogic/dls.xqy";
let $contents := <BOOK> <TITLE>Baz Goes to the Disco</TITLE> <CHAPTER1> <TITLE>Baz Wakes Up to James Brown and Feels Funky</TITLE> </CHAPTER1> </BOOK>
return dls:document-insert-and-manage( "/foo/bar/baz.xml", fn:true(), $contents)
(: Inserts 'baz.xml' into the database and places the document under management. :)
