xquery version "1.0-ml";
import module namespace gml = "http://www.opengis.net/gml"
       at "/MarkLogic/geospatial/gml.xqy";

gml:point(<gml:Point><gml:pos>12.5 -127.24</gml:pos></gml:Point>)

xquery version "1.0-ml";
import module namespace gml = "http://www.opengis.net/gml"
       at "/MarkLogic/geospatial/gml.xqy";

gml:circle(47, <gml:Point><gml:pos>12.5 -127.24</gml:pos></gml:Point>)

xquery version "1.0-ml";
import module namespace gml = "http://www.opengis.net/gml"
       at "/MarkLogic/geospatial/gml.xqy";

gml:polygon((
  <gml:Point><gml:pos>12.5 -127.24</gml:pos></gml:Point>,
  <gml:Point><gml:pos>15.25 -127.8</gml:pos></gml:Point>,
  <gml:Point><gml:pos>13.45 -126.1</gml:pos></gml:Point>,
  <gml:Point><gml:pos>12.5 -127.24</gml:pos></gml:Point>
))

xquery version "1.0-ml";
(: create a document with test data :) xdmp:document-insert("/points.xml", <root xmlns:gml="http://www.opengis.net/gml"> <item><gml:Point><gml:pos>10.5 30.0</gml:pos></gml:Point></item> <item><gml:Point><gml:pos>15.35 35.34</gml:pos></gml:Point></item> <item><gml:Point><gml:pos>5.11 40.55</gml:pos></gml:Point></item> </root> );
xquery version "1.0-ml"; import module namespace gml = "http://www.opengis.net/gml" at "/MarkLogic/geospatial/gml.xqy";
cts:search(doc("/points.xml")//item, gml:geospatial-query( gml:box( <gml:Envelope> <gml:lowerCorner>10.0 35.0</gml:lowerCorner> <gml:upperCorner>20.0 40.0</gml:upperCorner> </gml:Envelope>) )) (: returns the following node: <item><gml:Point><gml:pos>15.35 35.34</gml:pos></gml:Point></item> :) ,
cts:search(doc("/points.xml")//item, gml:geospatial-query(gml:box( <gml:Envelope> <gml:lowerCorner>10.0 40.0</gml:lowerCorner> <gml:upperCorner>20.0 35.0</gml:upperCorner> </gml:Envelope> ))) (: returns the following nodes (wrapping around the Earth): <item><gml:Point><gml:pos>10.5 30.0</gml:pos></gml:Point></item> :)

xquery version "1.0-ml";
(: create a document with test data :) xdmp:document-insert("/points.xml", <root xmlns:gml="http://www.opengis.net/gml"> <item><gml:Point><gml:pos>10.5 30.0</gml:pos></gml:Point></item> <item><gml:Point><gml:pos>15.35 35.34</gml:pos></gml:Point></item> <item><gml:Point><gml:pos>5.11 40.55</gml:pos></gml:Point></item> </root> );
xquery version "1.0-ml"; import module namespace gml = "http://www.opengis.net/gml" at "/MarkLogic/geospatial/gml.xqy";
cts:search(doc("/points.xml")//item, gml:geospatial-query-from-elements( <gml:Envelope> <gml:lowerCorner>10.0 35.0</gml:lowerCorner> <gml:upperCorner>20.0 40.0</gml:upperCorner> </gml:Envelope>) ) (: returns the following node: <item><gml:Point><gml:pos>15.35 35.34</gml:pos></gml:Point></item> :) ,
cts:search(doc("/points.xml")//item, gml:geospatial-query-from-elements( <gml:Envelope> <gml:lowerCorner>10.0 40.0</gml:lowerCorner> <gml:upperCorner>20.0 35.0</gml:upperCorner> </gml:Envelope>) ) (: returns the following nodes (wrapping around the Earth): <item><gml:Point><gml:pos>10.5 30.0</gml:pos></gml:Point></item> :)
