apidoc:example.pre:
xquery version "1.0-ml";
import module namespace kml = "http://earth.google.com/kml/2.0"
       at "/MarkLogic/geospatial/kml.xqy";

kml:polygon((
  <kml:Point><kml:coordinates>-127.24,12.5</kml:coordinates></kml:Point>,
  <kml:Point><kml:coordinates>-127.8,15.25</kml:coordinates></kml:Point>,
  <kml:Point><kml:coordinates>-126.1,13.45</kml:coordinates></kml:Point>,
  <kml:Point><kml:coordinates>-127.24,12.5</kml:coordinates></kml:Point>
))

xquery version "1.0-ml";
import module namespace kml = "http://earth.google.com/kml/2.0"
       at "/MarkLogic/geospatial/kml.xqy";

kml:polygon(
<kml:Polygon><kml:outerBoundaryIs><kml:LinearRing>
 <kml:coordinates>-127.24,12.5 -127.8,15.25 -126.1,13.45 -127.24,12.5</kml:coordinates>
</kml:LinearRing></kml:outerBoundaryIs></kml:Polygon>
)

xquery version "1.0-ml";
(: create a document with test data :) xdmp:document-insert("/points.xml", <root xmlns:kml="http://earth.google.com/kml/2.0"> <item><kml:Point><kml:coordinates>30.0,10.5</kml:coordinates></kml:Point></item> <item><kml:Point><kml:coordinates>35.34,15.35</kml:coordinates></kml:Point></item> <item><kml:Point><kml:coordinates>40.55,5.11</kml:coordinates></kml:Point></item> </root> );
xquery version "1.0-ml"; import module namespace kml = "http://earth.google.com/kml/2.0" at "/MarkLogic/geospatial/kml.xqy";
cts:search(doc("/points.xml")//item, kml:geospatial-query( kml:box( <kml:LatLongBox> <kml:north>20.0</kml:north> <kml:south>10.0</kml:south> <kml:east>40.0</kml:east> <kml:west>35.0</kml:west> </kml:LatLongBox>) )) (: returns the following node: <item><kml:Point><kml:coordinates>35.34,15.35</kml:coordinates></kml:Point></item> :) ,
cts:search(doc("/points.xml")//item, kml:geospatial-query(kml:box( <kml:LatLongBox> <kml:north>20.0</kml:north> <kml:south>10.0</kml:south> <kml:east>35.0</kml:east> <kml:west>40.0</kml:west> </kml:LatLongBox>) )) (: returns the following nodes (wrapping around the Earth): <item><kml:Point><kml:coordinates>30.0,10.5</kml:coordinates></kml:Point></item> :)

xquery version "1.0-ml";
(: create a document with test data :) xdmp:document-insert("/points.xml", <root xmlns:kml="http://earth.google.com/kml/2.0"> <item><kml:Point><kml:coordinates>30.0,10.5</kml:coordinates></kml:Point></item> <item><kml:Point><kml:coordinates>35.34,15.35</kml:coordinates></kml:Point></item> <item><kml:Point><kml:coordinates>40.55,5.11</kml:coordinates></kml:Point></item> </root> );
xquery version "1.0-ml"; import module namespace kml = "http://earth.google.com/kml/2.0" at "/MarkLogic/geospatial/kml.xqy";
cts:search(doc("/points.xml")//item, kml:geospatial-query-from-elements( <kml:LatLongBox> <kml:north>20.0</kml:north> <kml:south>10.0</kml:south> <kml:east>40.0</kml:east> <kml:west>35.0</kml:west> </kml:LatLongBox>) ) (: returns the following node: <item><kml:Point><kml:coordinates>35.34,15.35</kml:coordinates></kml:Point></item> :) ,
cts:search(doc("/points.xml")//item, kml:geospatial-query-from-elements( <kml:LatLongBox> <kml:north>20.0</kml:north> <kml:south>10.0</kml:south> <kml:east>35.0</kml:east> <kml:west>40.0</kml:west> </kml:LatLongBox>) ) (: returns the following nodes (wrapping around the Earth): <item><kml:Point><kml:coordinates>30.0,10.5</kml:coordinates></kml:Point></item> :)
